package com.exmaple.jarvis.chat.Model;public class Message extends Object {    private String id;    private String sender;    private String receiver;    private String message;    private String postedAt;    public Message(String id, String sender, String receiver, String message, String postedAt) {        this.id = id;        this.sender = sender;        this.receiver = receiver;        this.message = message;        this.postedAt = postedAt;    }    public String getId() {        return this.id;    }    public String getSender() {        return this.sender;    }    public String getReceiver() {        return this.receiver;    }    public String getMessage() {        return this.message;    }    public String getPostedAt() {        return this.postedAt;    }}