package com.exmaple.jarvis.chat.Model;public class Message extends Object {    private String mId;    private String mSender;    private String mReceiver;    private String mMessage;    private String mPostedAt;    public Message(String id, String sender, String receiver, String message, String postedAt) {        mId = id;        mSender = sender;        mReceiver = receiver;        mMessage = message;        mPostedAt = postedAt;    }    public String getId() {        return mId;    }    public String getSender() {        return mSender;    }    public String getReceiver() {        return mReceiver;    }    public String getMessage() {        return mMessage;    }    public String getPostedAt() {        return mPostedAt;    }}