package com.exmaple.jarvis.chat.Model;public class User extends Object {    private String mId;    private String mUsername;    private String mPassword;    private String mDisplayName;    private String mUserType;    private String mAvatar;    private String mEmail;    public User(String id, String username, String password, String displayName, String userType, String avatar, String email) {        mId = id;        mUsername = username;        mPassword = password;        mDisplayName = displayName;        mUserType = userType;        mAvatar = avatar;        mEmail = email;    }    public String getId() {        return mId;    }    public String getUsername() {        return mUsername;    }    public String getPassword() {        return mPassword;    }    public String getDisplayName() {        return mDisplayName;    }    public String getUserType() {        return mUserType;    }    public String getAvatar() {        return mAvatar;    }    public String getEmail() {        return mEmail;    }}